import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';

interface Product {
  id: number;
  name: string;
  description: string;
  price: number;
  stock: number;
  imageUrl: string;
}

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './home.component.html',
  styleUrl: './home.component.css'
})
export class HomeComponent {

  products: Product[] = [
    { id: 1, name: 'Laptop', description: 'High-performance laptop', price: 1200, stock: 5, imageUrl: 'data:image/jpeg;base64,' },
    { id: 2, name: 'Smartphone', description: 'Latest smartphone model', price: 800, stock: 10, imageUrl: 'data:image/jpeg;base64,' },
    { id: 3, name: 'Headphones', description: 'Noise-cancelling headphones', price: 150, stock: 15, imageUrl: 'data:image/jpeg;base64,' },
    { id: 4, name: 'Camera', description: 'High-quality DSLR camera', price: 600, stock: 3, imageUrl: 'data:image/jpeg;base64,' },
    { id: 5, name: 'Smartwatch', description: 'Smartwatch with health tracking', price: 200, stock: 8, imageUrl: 'data:image/jpeg;base64,' }
  ];
  
    buyProduct(product: Product) {
      if (product.stock > 0) {
        product.stock--;
        alert(`You bought a ${product.name}! Remaining stock: ${product.stock}`);
      } else {
        alert(`Sorry, ${product.name} is out of stock.`);
      }
    }
  
    sellProduct(product: Product) {
      product.stock++;
      alert(`You added more ${product.name} to stock. Total stock: ${product.stock}`);
    }
  }

